mapper.update:
update Year_Vacation
set yv_vacation_day =#{yv_vacation_day}
where yv_year =#{yv_year}

mapper.select:
SELECT * FROM ( SELECT A.*,ROWNUM AS RNUM,COUNT(*) OVER() AS TOTCNT from ( select mem_number,mem_name,mem_id,dept_name,position_name,mdi_carear as mem_carear,vp_totalday from member ,MEMBER_DETAIL_INFORMATION,VACATION_POLICY,department,position where mem_number= mdi_mem_number and dept_number = mem_dept_number and mem_position_number = position_number and mem_retirement = '1' and vp_number='vp1'
and mem_name = #{mem_name}
and mem_dept_number = #{mem_dept_number}
order by position_number asc ) A) where RNUM > #{startPage} AND RNUM <= #{endPage}

select *
from year_vacation
where yv_year between 0 and #{str}

select count(*) from member ,MEMBER_DETAIL_INFORMATION,VACATION_POLICY,department,position where mem_number= mdi_mem_number and dept_number = mem_dept_number and mem_position_number = position_number and mem_retirement = '1' and vp_number='vp1'
and mem_name = #{mem_name}
and mem_dept_number = #{mem_dept_number}
order by position_number
