mapper.select:
SELECT * FROM ( SELECT A.*,ROWNUM AS RNUM,COUNT(*) OVER() AS TOTCNT from ( select * from member,POSITION,DEPARTMENT where mem_position_number = position_number and mem_dept_number = dept_number and mem_retirement = '0'
and mem_name = #{mem_name}
and mem_dept_number = #{mem_dept_number}
ORDER BY mem_position_number asc ) A) where RNUM > #{startPage} AND RNUM <= #{endPage}

mapper.update:
update member
set MEM_RETIREMENT = '1'
where mem_id = #{mem_id}

mapper.select:
select count(*) from member,POSITION,DEPARTMENT where mem_position_number = position_number and mem_dept_number = dept_number and mem_retirement = '0'
and mem_name = #{mem_name}
and mem_dept_number = #{mem_dept_number}
order by mem_position_number asc
