mapper.select:
SELECT * FROM ( SELECT A.*,ROWNUM AS RNUM,COUNT(*) OVER() AS TOTCNT from( SELECT * FROM MEMBER,DEPARTMENT,VACATION,VACATION_POLICY,position WHERE DEPT_NUMBER= MEM_DEPT_NUMBER AND VACATION_MEM_NUMBER = MEM_NUMBER AND VACATION_POLICY.VP_NUMBER = VACATION.VACATION_VP_NUMBER and position_number = MEMBER.MEM_POSITION_NUMBER and vacation_end between #{dept_startdate} and #{dept_enddate}
AND mem_name like '%'||#{search}||'%'
AND vp_kind like '%'||#{search}||'%'
AND 1 = 1
ORDER BY lpad(substr(mem_number,4,99999999),5,0)asc ) A) where RNUM > #{startPage} AND RNUM <= #{endPage}
